Bash(cd "/d/AI教學與專案實作/專案_學系探索遊戲" && npm install qrcode --save-dev 2>&1 | tail -5 && \\
node << 'EOF'
const QRCode = require\('qrcode'\);

const url = 'https://chatgpt3a01.github.io/college-major-discovery-game/';

QRCode.toFile\('game-qr-code.png', url, {
  errorCorrectionLevel: 'H',
  type: 'image/png',
  width: 300,
  margin: 2,
  color: {
    dark: '#000000',
    light: '#FFFFFF'
  }
}, function \(err\) {
  if \(err\) throw err;
  console.log\('✅ QR CODE 生成成功！'\);
  console.log\('🔗 掃描此 QR CODE 可進入遊戲'\);
}\);
EOF)
